(()=>{transactions=[];const t=()=>{transactions=JSON.parse(localStorage.getItem("transactions"))||[];const t=document.querySelector("#transactionsList");t.innerHTML="",transactions.forEach((e=>{const n=document.createElement("li");n.innerText=JSON.stringify(e),t.appendChild(n)}))};document.querySelector("#refreshBtn").addEventListener("click",t),document.querySelector("#cleanBtn").addEventListener("click",(()=>{transactions=[],document.querySelector("#transactionsList").innerHTML="",localStorage.removeItem("transactions")})),t()})();